import { Movie } from "../types";
import MovieDisplay from "./MovieDisplay";
import AddMovieForm from "./AddMovieForm";
import "./App.css";
import { useState } from "react";


function App() {
  const defaultMovies: Movie[] = [
    {
      title: "Vagabond",
      director: "Takehiko Inoue",
      duration: 120,
      pictureUrl:
        "data:image/jpeg;base64,",
      budget: 150,
    },
    {
      title: "Dragon ball Super",
      director: "Akira Toriyama",
      duration: 127,
      pictureUrl:
        "https://preview.redd.it/e9f9vune5fa11.jpg?width=1080&crop=smart&auto=webp&s=06ac1bd7d69b86e3bb2b16f1750e34982f248918",
    },
    {
      title: "Juujika no Rokunin",
      director: "Shiryu Tanaka",
      duration: 140,
      pictureUrl:
        "data:image/jpeg;base64,",
    },
    {
      title: "Orb: On the Movements of the Earth",
      director: "Uoto",
      duration: 90,
      pictureUrl:
        "https://m.media-amazon.com/images/M/MV5BNjRmMThlN2EtYTM0Ni00MjBjLWFmZmMtOGYxZWI1MzUzNzJhXkEyXkFqcGc@._V1_.jpg",
    },
    {
      title: "Dandadan",
      director: "Yukinobu Tatsu",
      duration: 120,
      pictureUrl:
        "data:image/jpeg;base64,",
    },
  ];

  const [movies, setMovies] = useState<Movie[]>(defaultMovies);

  const addMovie = (movie: Movie) => {
    setMovies([...movies, movie]);
  }
  return (
    <div>
      <h1>My favorite movies</h1>
      <MovieDisplay movies={movies} />
      <AddMovieForm onAddMovie={addMovie}/>
    </div>
  );
}

export default App;
